public class RangeOfPrimitiveDatatypes {

	public static void main(String[] args) {
    
		System.out.println("Max Value of byte"+Byte.MAX_VALUE);        // Maximum Value of BYTE data type
		System.out.println("Min Value of byte"+Byte.MIN_VALUE);       // Minimum Value of BYTE data type
		
		System.out.println("Max Value of Character"+Character.MAX_VALUE);        // Maximum Value of CHARACTER data type
		System.out.println("Min Value of Character"+Character.MIN_VALUE);       // Minimum Value of CHARACTER data type
		
		System.out.println("Max Value of Short"+Short.MAX_VALUE);          // Maximum Value of SHORT data type
		System.out.println("Min Value of Short"+Short.MIN_VALUE);         // Minimum Value of SHORT data type
		
		System.out.println("Max Value of Integer"+Integer.MAX_VALUE);        // Maximum Value of INTEGER data type
		System.out.println("Min Value of Integer"+Integer.MIN_VALUE);       // Minimum Value of INTEGER data type
		
		System.out.println("Max Value of Long"+Long.MAX_VALUE);        // Maximum Value of LONG data type
		System.out.println("Min Value of Long"+Long.MIN_VALUE);       // Minimum Value of LONG data type
		
		System.out.println("Max Value of Float"+Float.MAX_VALUE);        // Maximum Value of FLOAT data type
		System.out.println("Min Value of Float"+Float.MIN_VALUE);       // Minimum Value of FLOAT data type
		
		System.out.println("Max Value of Double"+Double.MAX_VALUE);        // Maximum Value of DOUBLE data type
		System.out.println("Min Value of Double"+Double.MIN_VALUE);       // Minimum Value of DOUBLE data type

    
    // For BOOLEAN data type there are only 2 values i.e, TRUE or FALSE

	}

}
